import React, { useState } from 'react'

const Login = () => {

  const [currentState,setCurrentState]=useState('Sign Up');

  const onSubmitHandler=async(event)=>{
    event.preventDefault();
  }

  return (
    <form  className='flex flex-col items-center w-[90%] sm:max-w-96 m-auto mt-14 gap-4 text-gray-800'>
        <div className='inline-flex items-center gap-2 mb-2 mt-10'>
          <p className='parata-regular text-3xl'>{currentState}</p>
          <hr className='border-none h-[1.5px] w-8 bg-gray-800' />
        </div>
        {currentState==='Login'?'':<input type="text" className='w-full px-3 py-2 border rounded-md rounded-md border-gray-800 ' placeholder='Name' required/>}
        <input type="email" className='w-full px-3 py-2 border rounded-md border-gray-800' placeholder='Email' required/>
        <input type="password" className='w-full px-3 py-2 border rounded-md border-gray-800' placeholder='Password' required/>

        <div className='w-full flex justify-between text-sm mt-[-8px]'>
          <p className='pl-1 cursor-pointer'>Forgot Password</p>
          {
            currentState==='Login'?
            <p onClick={()=>setCurrentState('Sign Up')} className='pr-1 cursor-pointer'>Create Account</p>:
            <p onClick={()=>setCurrentState('Login')} className='pr-1 cursor-pointer'>Login Here</p>
          }
        </div>
        <button className='bg-black text-white rounded-md font-light px-8 py-2 mt-4'>{currentState==='Login'?'Sign In':'Sign Up'}</button>
    </form>
  )
}

export default Login
