            <img src={assets.search_icon} className='w-4' alt="" />
        </div>
            <img onClick={()=>setShowSearch(false)} src={assets.cross_icon} className='inline w-3 cursor-pointer
            ' alt="" />
    </div>
  ):null
}

export default SearchBar
